import "./login.css";
import { NavLink, useNavigate } from "react-router-dom";
import { useState } from "react";

const Login = () => {

    // const navigate = useNavigate();

    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const handleClick = async (e) => {
        e.preventDefault();

        try {
            const res = await axios.post('http://localhost:8080/login', {
                email,
                password
            });

            if (res.status === 400) {
                window.alert("Invalid Credentials");
            } else {
                window.alert("Login Successful");
            }

        } catch (error) {
            window.alert("Error: " + error.message);
        }
    }


    return (
        <div className="login">
            <h1>Login</h1>


            <form method="POST" className="form">
                <input name="email" type="email" placeholder="E-mail" value={email} onChange={(e) => { setEmail(e.target.value) }} className="input" />
                <input name="password" type="password" placeholder="Password" value={password} onChange={(e) => { setPassword(e.target.value) }} className="input" />
                <button type="submit" className="btn" onClick={handleClick}>Login</button>
            </form>



            <p>Or</p>
            <p>New User? <NavLink to="/register">SignUp</NavLink> </p>
        </div>
    )
}

export default Login
