import "./login.css";
import { Field, Form, Formik} from "formik";
import * as yup from "yup"
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";

const Login = () => {

    const navigate = useNavigate();

    // const handleSubmit = async (user) => {
    //     const res = await axios.post("http://localhost:8080/auth/login", user);
    //     const data = res.json

    //     if(res.status === 400 || !data){
    //         console.log(data)
    //         window.alert("Invalid Credentials")
    //     }else{
    //         window.alert("Login Successfull");
    //         navigate("/")
    //     }
    // }


    return(
        <div className="login">
            <h1>Login</h1>
            <Formik
                initialValues={
                    {
                        email:"",
                        password:""
                    }
                }
                validationSchema={
                    yup.object({
                        email: yup.string(),
                        password: yup.string()
                    })
                }
                onSubmit={(user) => {
                    axios.post("http://localhost:8080/auth/login", user)
                         .then((res) => {
                            if(res.status === 400){
                                alert("Invalid Credentials")
                            }else{
                                navigate("/");
                            }
                         })
                }}
            >
            {
                <Form className="form">
                    <Field name="email" type="email" placeholder="E-mail" className="input" />
                    <Field name="password" type="password" placeholder="Password" className="input" />
                    <button type="submit" className="btn">Login</button>
                </Form>
            }

            </Formik>
            <p>Or</p>
            <p>New User? <Link to="/register">SignUp</Link> </p>
        </div>
    )
}

export default Login
