import "./login.css";
import { Field, Form, Formik, useFormik } from "formik";
import * as yup from "yup"

const Login = () => {
    return(
        <div>
            <h1>Login</h1>
            <Formik
                initialValues={
                    {
                        email:"",
                        password:""
                    }
                }
                validationSchema={
                    yup.object({
                        email: yup.string(),
                        password: yup.string()
                    })
                }
                onSubmit={
                    (values) => {
                        alert(values)
                    }
                }
            >
            {
                <Form>
                    <Field name="email" type="email" placeholder="E-mail" />
                    <Field name="password" type="password" placeholder="Password" />
                    <button>Login</button>
                </Form>
            }

            </Formik>
        </div>
    )
}

export default Login
