import "./register.css";
import { NavLink, useNavigate } from "react-router-dom";
import { useState } from "react";
import axios from "axios"

const Register = () => {

    const navigate = useNavigate();

    const [user, setUser] = useState({
        name:"",
        email:"",
        password:"",
        cpassword:""
    });

    let name, value
    const handleChange = (e) => {
        name = e.target.name;
        value = e.target.value;
        setUser({...user, [name]: value});
    }

    const handleClick = async (e) => {
        e.preventDefault();

        const {name, email, password, cpassword} = user;

        try {
            const res = await axios.post("/register", { name, email, password, cpassword }, {
                headers: {
                    "Content-Type": "application/json"
                }
            });

            console.log(res.data);

            window.alert("Registered successfully");
            console.log("Registered");

            navigate("/login");

        } catch (err) {
            console.error(err);
            window.alert("Invalid Registration");
            console.log("Not registered");
        }
    }

    return (
        <div className="register">
            <h1>SignUp</h1>

            <form method="POST" className="form">
                <input name="name" type="text"  placeholder="Name" value={user.name} onChange={handleChange} className="input" required />
                <input name="email" type="email"  placeholder="E-mail" value={user.email} onChange={handleChange} className="input" required />
                <input name="password" type="password"  placeholder="Password" value={user.password} onChange={handleChange} className="input" required />
                <input name="cpassword" type="password"  placeholder="Confirm Password" value={user.cpassword} onChange={handleChange} className="input" required />
                <button type="submit" className="btn" onClick={handleClick} >SignUp</button>
            </form>

            <p>Or</p>
            <p>Already a user? <NavLink to="/login">Login</NavLink></p>
        </div>
    )
}

export default Register
