import "./register.css";
import { Field, Form, Formik } from "formik";
import * as yup from "yup";
import axios from "axios"
import { Link, useNavigate } from "react-router-dom";

const Register = () => {

    const navigate = useNavigate();

    return (
        <div className="register">
            <h1>SignUp</h1>
            <Formik
                initialValues={
                    {
                        name: "",
                        email: "",
                        password: "",
                        cpassword: ""
                    }
                }
                validationSchema={
                    yup.object({
                        name: yup.string(),
                        email: yup.string(),
                        password: yup.string(),
                        cpassword: yup.string()
                    })
                }
                onSubmit={
                    (user) => {
                        if (user.password === user.cpassword) {
                            axios.post("http://localhost:8080/auth/register", user)
                                .then(() => {
                                    alert("Registered Successfully!!!!")
                                    navigate("/login");
                                })
                        } else {
                            alert("Password and Confirm password does not match")
                        }
                    }
                }
            >
                {
                    <Form className="form">
                        <Field name="name" type="text" placeholder="Name" className="input" required />
                        <Field name="email" type="email" placeholder="E-mail" className="input" required />
                        <Field name="password" type="password" placeholder="Password" className="input" required />
                        <Field name="cpassword" type="password" placeholder="Confirm Password" className="input" required />
                        <button type="submit" className="btn">SignUp</button>
                    </Form>
                }

            </Formik>
            <p>Or</p>
            <p>Already a user? <Link to="/login">Login</Link></p>
        </div>
    )
}

export default Register
