import "./register.css";
import { useNavigate } from "react-router-dom";
import { useState } from "react";
import axios from "axios"

const Register = () => {

    const [user, setUser] = useState({
        name:"",
        email:"",
        password:"",
        cpassword:""
    });

    const handleChange = (e) => {
        const {name, value} = e.target
        setUser({...user, [name]: value});
    }

    const handleClick = () => {

        const {name, email, password, cpassword} = user;

        if(name && email && password && (password === cpassword)){
            alert("posted")
        }else{
            alert("invalid")
        }

        // axios.post("http://localhost:8080/auth/register", user)
        //      .then((res) => {
        //         console.log(res.data)
        //      })

        // if(res.status === 422){
        //     window.alert("Invalid registration");
        // }else{
        //     window.alert("Registration Successfull");
        //     navigate("/login");
        // }
    }

    const navigate = useNavigate();

    return (
        <div className="register">
            <h1>SignUp</h1>

            <form method="POST" className="form">
                <input name="name" type="text"  placeholder="Name" value={user.name} onChange={handleChange} className="input" required />
                <input name="email" type="email"  placeholder="E-mail" value={user.email} onChange={handleChange} className="input" required />
                <input name="password" type="password"  placeholder="Password" value={user.password} onChange={handleChange} className="input" required />
                <input name="cpassword" type="password"  placeholder="Confirm Password" value={user.cpassword} onChange={handleChange} className="input" required />
                <button type="submit" className="btn" onClick={handleClick}>SignUp</button>
            </form>

            <p>Or</p>
            <p>Already a user? <a href="/login">Login</a></p>
        </div>
    )
}

export default Register
