// import "./login.css";
import { NavLink, useNavigate } from "react-router-dom";
import { useState } from "react";
import axios from "axios";

const Login = () => {

    const navigate = useNavigate();

    const [userLogin, setUserLogin] = useState({
        email: "",
        password: "",

    });

    const { email, password } = userLogin;

    let name, value
    const handleChange = (e) => {
        name = e.target.name;
        value = e.target.value;
        setUserLogin({ ...userLogin, [name]: value });
    }

    const login = async (e) => {
        e.preventDefault();

        try {
            const res = await axios.post('http://localhost:8080/login', {
                email,
                password
            }, {
                headers: {
                    "Content-Type": "application/json"
                }
            });

            localStorage.setItem("token", res.data.token);
            window.alert("Login Successful");
            navigate("/")

        } catch (error) {
            console.log(error);

            window.alert("Invalid Credentials");
        }
    }


    return (
        <div className="conntainer-fluid w-50 m-auto mt-5">
            <div className="text-center">
                <h1>Login</h1>
            </div>
            <form method="POST" className="form-control">
                <div class="mb-3">
                    <label for="email" className="form-label">Email address</label>
                    <input name="email" id="email" type="email" placeholder="E-mail" value={email} onChange={handleChange} className="form-control w-50" />
                </div>
                <div class="mb-3">
                    <label for="password" className="form-label">Password</label>
                    <input name="password" id="password" type="password" placeholder="Password" value={password} onChange={handleChange} className="form-control w-50" />
                </div>
                {/* <button type="submit" className="btn" onClick={login}>Login</button> */}
                <button type="submit" className="btn btn-primary mb-3" onClick={login}>Log in</button>
            </form>

            <p>Or</p>
            <p>New User? <NavLink to="/register">SignUp</NavLink> </p>
        </div>
    )
}

export default Login
