import "./register.css";
import { NavLink, useNavigate } from "react-router-dom";
import { useState } from "react";
import axios from "axios"

const Register = () => {

    const [user, setUser] = useState({
        name:"",
        email:"",
        password:"",
        cpassword:""
    });

    let name, value
    const handleChange = (e) => {
        name = e.target.name;
        value = e.target.value;
        setUser({...user, [name]: value});
    }

    const handleClick = async (e) => {
        e.preventDefault();

        const {name, email, password, cpassword} = user;

        const res = await fetch("/register",{
            method:"POST",
            headers:{
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                name, email, password, cpassword
            })
        });

        const data = await res.json();

        console.log(data);

        if(res.status === 422 || !data){
            window.alert("Invalid Registration");
            console.log("Not register");
        }else{
            window.alert("Registered successfull");
            console.log("Register");

            navigate("/login");

        }

    }

    const navigate = useNavigate();

    return (
        <div className="register">
            <h1>SignUp</h1>

            <form method="POST" className="form">
                <input name="name" type="text"  placeholder="Name" value={user.name} onChange={handleChange} className="input" required />
                <input name="email" type="email"  placeholder="E-mail" value={user.email} onChange={handleChange} className="input" required />
                <input name="password" type="password"  placeholder="Password" value={user.password} onChange={handleChange} className="input" required />
                <input name="cpassword" type="password"  placeholder="Confirm Password" value={user.cpassword} onChange={handleChange} className="input" required />
                <input type="submit" value="SignUp" className="btn" onClick={handleClick} />
            </form>

            <p>Or</p>
            <p>Already a user? <NavLink to="/login">Login</NavLink></p>
        </div>
    )
}

export default Register
