import "./login.css";
import { Field, Form, Formik} from "formik";
import * as yup from "yup"
import axios from "axios";

const Login = () => {
    return(
        <div className="login">
            <h1>Login</h1>
            <Formik
                initialValues={
                    {
                        email:"",
                        password:""
                    }
                }
                validationSchema={
                    yup.object({
                        email: yup.string(),
                        password: yup.string()
                    })
                }
                onSubmit={
                    (user) => {
                        axios.post("http://localhost:8080/auth/login", user);
                    }
                }
            >
            {
                <Form className="form">
                    <Field name="email" type="email" placeholder="E-mail" className="input" />
                    <Field name="password" type="password" placeholder="Password" className="input" />
                    <button className="btn">Login</button>
                </Form>
            }

            </Formik>
            <p>Or</p>
            <p>New User? SignUp</p>
        </div>
    )
}

export default Login
