import "./login.css";
import { NavLink, useNavigate } from "react-router-dom";
import { useState } from "react";
import axios from "axios";

const Login = () => {

    const navigate = useNavigate();

    const [userLogin, setUserLogin] = useState({
        email: "", password: ""
    });

    const {email, password} = userLogin;

    let name, value
    const handleChange = (e) => {
        name = e.target.name;
        value = e.target.value;
        setUserLogin({...userLogin, [name]: value});
    }

    const handleClick = async (e) => {
        e.preventDefault();

        try {
            const res = await axios.post('http://localhost:8080/login', {
                email,
                password
            },{
                headers: {
                    "Content-Type": "application/json"
                }
            });

            if (res.status === 200) {
                localStorage.setItem("token", res.data.token)
                window.alert("Login Successful");
                console.log(res.data);
                navigate("/")
            } else {
                window.alert("Invalid Credentials");
            }




        } catch (error) {
            console.log(error);
            // window.alert("Invalid Credentials");
        }
    }


    return (
        <div className="login">
            <h1>Login</h1>


            <form method="POST" className="form">
                <input name="email" type="email" placeholder="E-mail" value={email} onChange={handleChange} className="input" />
                <input name="password" type="password" placeholder="Password" value={password} onChange={handleChange} className="input" />
                <button type="submit" className="btn" onClick={handleClick}>Login</button>
            </form>



            <p>Or</p>
            <p>New User? <NavLink to="/register">SignUp</NavLink> </p>
        </div>
    )
}

export default Login
